import React, { useState, useEffect } from 'react';

import { Link, HashRouter, Route, useHistory } from "react-router-dom";
import { useMediaQuery } from "react-responsive";
import { useCookies } from 'react-cookie';
import axios from "axios";
import Router from "./Router";

import { TextField } from "@material-ui/core";
import Header from './Header';
import Guide from './Guide';

import "../css.css";

const api = axios.create({
    headers: {
        "Access-Control-Allow-Origin" : "*",
    }
});

function Main() {
    const [userId, setUserId] = useState();
    const [userPw, setUserPw] = useState();
    const {userData, setUserData} = useState();
    const history = useHistory();

    useEffect(() => {
        removeCookie("s_email");
    }, []);

    const login = (x) => {
        x.preventDefault();

        let userData;

        userData = {
            s_email: userId,
            s_password: userPw
        };
        setUserData(userData);

        axios
        .post("login", userData)
        .then((res) => {
            localStorage.setItem("token", res.userData.token);
            console.log(res.userData.login);


        const { accessToken } = res.userData;
        axios.defaults.headers.common[
            "Authorization"
        ] = `Bearer ${accessToken}`;
        console.log(accessToken);

        if(res.userData.login == "Success" && res.userData.token) {
            console.log(res.userData);
            alert("로그인 되셨습니다.");

            setCookie("s_email", userData.s_email);
            setCookie("s_name", res.userData.s_name);
            history.push("/student");
            setCookie("isLogIn", true);
        }
        else {
            alert("아이디와 비밀번호를 확인해 주세요")
            history.push("/");
        }
        })
        .catch((err) => {

        })
    }

    return (
        <div>
            <Header />
            <div className="MainBox">
                <span className="BoxTextTitle">
                온라인 코딩 시험 플랫폼 Re:Coder 입니다
                </span>
                <span className="BoxTextConnect">
                온라인 시험 감독을 위해 카메라 사용 권한 허용이 필요합니다.
                </span>

                <div className="MainSubBox">
                    <span className="SubBoxText">
                    원활한 시험 응시를 위하여 ID와 PW를 입력 후 로그인 해 주세요.
                    </span>
                </div>
                <div className="LoginBox">
                    <span className="BoxTextCode">
                    <span style={{color: "red"}}>✻</span> 로그인</span>
                    <div className="InputBox">
                        <TextField id="userId" variant="outlined" size="small" 
                            label="ID" style={{width: "100%", margin: "10px 0px"}}
                            onChange={(x) => setUserId(x.target.value)}/>
                        <TextField id="userPw" variant="outlined" size="small" 
                            label="PW" style={{width: "100%"}} type="password"
                            onChange={(x) => setUserPw(x.target.value)}/>
                    </div>
                </div>
                <div>
                    <HashRouter>
                    <Link to="/Guide">
                    <button className="MainButton" >로그인</button>
                    </Link>
                    </HashRouter>
                </div>
            </div>
        </div>
    )
}

export default Main;
