import React from 'react';

import Header from './Header';
import '../css.css';
import Video from '../media/sample_v_1.mp4';

function showblock() {
    let x = document.getElementById("testbox");
    if (x.style.display == "none") {
        x.style.display = "block";
    }
}

function Test() {
    return (
        <div style={{display: 'none'}} id="testbox" className="TestBox">
            <span className="TestTitle">
                핸드폰 카메라 설정 완료
            </span>
            <span className="TestText">
                이 창을 닫지 마시고, 컴퓨터에서 모바일 기기의 촬영이 정상적으로 이루어지는지 확인하세요.
            </span>
        </div>
    );
}

function Camera() {
    return (
        <div>
            <Header />
            <div className="MainBox">
                <div className="CameraBox">
                <span className="CameraTitle">
                    [카메라 촬영 설정]
                </span>
                </div>
                <span>
                    카메라 접근 허용을 해야 정상적으로 시험에 응시가 가능합니다.
                </span>
                <Test />
                <button className="TestButton" onClick={() => Test}>
                    임시 허용 버튼
                </button>
                <div className="VideoBox">
                    <video style={{width:'90%'}} src={Video} type="video/mp4" autoPlay muted loop/>
                </div>
            </div>
        </div>
    );
}

export default Camera;
