import Main from './Main';
import Guide from './Guide';

function Router() {
    return (
        <div>
            <Header />
            <HashRouter>
                <Link to="/"></Link>
                <Link id="toGuide" to="/Guide"></Link>
                <Route to="/" exact={true} component={Main} />
                <Route to="/Guide" exact={true} component={Guide} />
            </HashRouter>
        </div>
    );
}

export default Router;
