import logo from './logo.svg';
import Media from "react-media";
import { HashRouter } from "react-router-dom";
import { useMediaQuery } from "react-responsive";
import './App.css';


function App() {
  const isPc = useMediaQuery({
    query: "(min-device-width: 1024px) and (max-device-width: 1279px)"
  });
  const isTablet = useMediaQuery({
    query: "(min-device-width: 768px) and (max-device-width: 1023px)"
  });
  const isTabletPc = useMediaQuery({
    query: "(min-device-width: 768px) and (max-device-width: 1279px)"
  });
  const isMobileTablet = useMediaQuery({
    query: "(max-device-width: 1023px)"
  });
  const isMobile = useMediaQuery({
    query: "(max-device-width: 767px)"
  });

  return(
    <div className="App">
      {isPc && <p>PC</p>}
      {isTablet && <p>Tablet</p>}
      {isTabletPc && <p>Tablet_PC</p>}
      {isMobileTablet && <p>Mobile_Tablet</p>}
      {isMobile && <p>Mobile</p>}
    </div>
  );
}

export default App;
