import React from 'react';
import { HashRouter, Link, Route } from "react-router-dom";
import { useMediaQuery } from "react-responsive";
import Header from './Header';
import Footer from './Footer';
import Mainbox from './Mainbox';
import "../css.css";
import TextInput from './TextInput';
import Guide from './Guide';

function Main() {
    return (
        <div>
            <Header />
            <HashRouter>
            <Mainbox>
            {/* <div className="MainBox"> */}
            <span className="BoxTextTitle">
            온라인 코딩 시험 플랫폼 Re:Coder 입니다
            </span>
            <span className="BoxTextConnect">
            온라인 시험 감독을 위해 휴대폰 카메라 접근권한에 대한 허가가 필요합니다.
            </span>

            <div className="MainSubBox">
                <span className="SubBoxText">
                PC화면에 나타난 12자리 인증코드를 입력해주세요.
                </span>
            </div>

            <span className="BoxTextCode">
             <span style={{color: "red"}}>✻</span> 인증 코드
            </span>
            <div className="InputBox">
                <TextInput className="Input" />
                <TextInput className="Input" />
                <TextInput className="Input" />
            </div>
            <Link to="./Guide.js">
                <button className="MainButton" >연결 요청</button>
            </Link>
            <Route path="/" exact={true} component={Guide}/>
        {/* </div> */}
            </Mainbox>
            </HashRouter>
        </div>
    )
}

export default Main;
